// dayjs stub
(function() { 
  if (typeof window !== 'undefined') { 
    window.dayjs = window.dayjs || function(date) { 
      return {
        format: function() { return date ? date.toString() : ''; },
        add: function() { return this; },
        subtract: function() { return this; }
      }; 
    }; 
  } 
})();
